import React, { useState, useEffect, useContext } from 'react';
import './AgentDashboard.css';
import api from '../api';
import TicketModal from './TicketModal';
import { UserContext } from '../UserContext';

const AgentDashboard = () => {
  const [tickets, setTickets] = useState([]);
  const [selectedTicket, setSelectedTicket] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const { user } = useContext(UserContext);

  useEffect(() => {
    setLoading(true);
    fetchTickets();
  }, []);

  const fetchTickets = async () => {
    try {
      const response = await api.get('/tickets/ticketsAssignedToMe'); // Assuming this route exists
      setTickets(response.data);
    } catch (error) {
      setError('Error fetching tickets.');
      console.error('Error fetching tickets:', error);
    } finally {
      setLoading(false);
    }
  };

  const handleViewTicket = (ticket) => {
    setSelectedTicket(ticket);
    setShowModal(true);
  };

  const handleCloseModal = () => {
    fetchTickets(); // Refetch tickets to ensure the UI is updated
    setShowModal(false);
    setSelectedTicket(null);
  };

  const handleTicketAction = (updatedStatus) => {
    // Optimistically update the ticket status locally
    const updatedTickets = tickets.map(ticket =>
      ticket._id === selectedTicket._id ? { ...ticket, status: updatedStatus } : ticket
    );
    setTickets(updatedTickets);

    // Close the modal after action
    handleCloseModal();
  };

  if (loading) return <p>Loading...</p>;
  if (error) return <p>{error}</p>;

  return (
    <div className="dashboard-container">
      <header className="header">
        <h2>Welcome, {user?.name || 'Agent'}</h2>
      </header>

      <div className="ticket-list">
        <h3>Your Tickets</h3>
        <table className="table">
          <thead>
            <tr>
              <th>Ticket ID</th>
              <th>Title</th>
              <th>Status</th>
              <th>View</th>
            </tr>
          </thead>
          <tbody>
            {tickets.length > 0 ? (
              tickets.map(ticket => (
                <tr key={ticket._id}>
                  <td>{ticket._id}</td>
                  <td>{ticket.title}</td>
                  <td>{ticket.status}</td>
                  <td>
                    <button onClick={() => handleViewTicket(ticket)}>View</button>
                  </td>
                </tr>
              ))
            ) : (
              <tr>
                <td colSpan="4">No tickets assigned to you.</td>
              </tr>
            )}
          </tbody>
        </table>
      </div>

      {/* Modal for ticket details */}
      {showModal && (
        <TicketModal
          ticket={selectedTicket}
          onClose={handleCloseModal}
          onAction={handleTicketAction} // Callback to handle ticket status change
        />
      )}
    </div>
  );
};

export default AgentDashboard;
